// Import necessary modules
import React from 'react';
import {Routes, Route, Link, useParams } from 'react-router-dom';

// Sample components
const Home = () => <h2>Home Page</h2>;

const UserProfile = () => {
  // Use the useParams hook to get the value of the 'id' parameter from the URL
  const { id } = useParams(); // here id it's an object used so written in {} soo if if you want to add another param
  //you could just write {id,id1}

  return (
    <div>
      <h2>User Profile</h2>
      <p>User ID: {id}</p>
    </div>
  );
};

// App component
const App = () => {
  return (
    <>
      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/user/12999999999999993">User Profile </Link>
          </li>
          <li>
            <Link to="/user/456">User Profile (ID: 456)</Link>
          </li>
        </ul>
      </nav>

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/user/:id" element={<UserProfile  />} />
      </Routes>
    </>
  );
};

export default App;
