import React, { useEffect, useState } from "react";
import axios from "axios";

const App = () => {
  const [num, setNum] = useState(1); // Set initial value for num
  const [name, setName] = useState("");
  const [moves, setMoves] = useState([]);

  useEffect(() => {
    async function getData() {
      try {
        const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${num}`);
        setName(response.data.name);
        setMoves(response.data.moves);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    }

    getData();
  }, [num]); // Add num as a dependency to run useEffect when num changes

  return (
    <>
      <h1>
        You Choose <span style={{ color: "red" }}>{num}</span> value
      </h1>
      <h1>
        My name is <span style={{ color: "red" }}>{name}</span>
      </h1>
      <h1>
        I have{" "}
        <span style={{ color: "red" }}>
          {moves.map((move, index) => (
            <span key={index}>{move.move.name}, </span>
          ))}
        </span>
      </h1>
      <select value={num} onChange={(e) => setNum(e.target.value)}>
        <option value="1">1</option>
        <option value="25">25</option>
        <option value="3">3</option>
        <option value="4">4</option>
        <option value="5">5</option>
      </select>
    </>
  );
};

export default App;
