import React, { useEffect,useState } from "react";


const App=()=>{
    const [n,Sn]=useState(0)
    const[sn,sns]=useState(0)

    useEffect(()=>{
        alert("clicked",[num])
    })

      return(
        <>
           <button onClick={()=>{
            Sn(n+1)
           }}>click {n}
            <button onClick={()=>{
            sns(n+1)
           }}> click {sn}</button>

           </button>
        </>
      )
   

    }

export default App
