import React from 'react'
import Card from "./Card" 
import Sdata from "./Sdata"



const App=(val) => {/* All Sdata object will come in "val" since using "map func" */
    <>
     <h1 className="heading_style"> Most Watched Netflix Webseries </h1>
     {Sdata.map}

    </>
     return (
        <Card 
          key={val.id}
          imgsrc={val.imgsrc}
          title={val.title}
          sname= {val.sname}
          link={val.link} 
        /> 
       )
   
}
       
   
    

export default App


