    const {fname}=useParams()
    const location=useLocation()
     console.log(location)

  return (
   
      <>
        <ul>
           <li>
              <NavLink to="/Sameer"> {fname}</NavLink>
           </li> {/*  */}
        </ul>
        <p> current location {location.pathname}</p>
        {location.pathname=== "/Smeer" ? (<button> click me</button>):null}
        
      </> 
    
  );
};

export default User;
