import { NavLink, useLocation, useParams } from "react-router-dom";

const User = () => {
     
    const {fname}=useParams()
    const location=useLocation()
    
     console.log(location) 
     /* go to inspect check object you'll find all object in it */

  return (
   
      <>
        <ul>
           <li>
              <NavLink to="/Sameer"> {fname}</NavLink>
           </li> {/*  */}
        </ul>
        <p> current location {location.pathname}</p> {/* here u'll get pathname */}
        {location.pathname=== "/Sameer" ? (<button onClick={()=>{alert("u got it")}}> click me</button>):null}
        
      </> 
    
  );
};

export default User;
