import React from 'react'
    

function App() {
    let date = new Date(2024,1,1,22)
    date = date.getHours()
    let greeting=''
    
    const cssStyle={
    
    }
    
    if(date >= 1 && date<12){
          greeting='Good Moring' 
          cssStyle.color='green'
    
    }else if(date>=12 && date<19){
            greeting="Godd Evening"
            cssStyle.color='orange'
    
    }else{
            greeting="Good Night"
            cssStyle.color='blue'
    
    }

     return (

        <div  className= "time">
           <h1> Hello Sir,<span style = {cssStyle}> {greeting}</span></h1>
            
        </div>,
             document.getElementById('root')
     )
}

export default App
