import React,{useState} from "react"

const App = () => {
     
     const [count,setCount] =useState(0)
     const Numclick=() => {
    
       setCount(count++)
     }
      return (
        <>
           <h1 className="heading_style" > {count}</h1> 
           <button onClick={Numclick} >Click me</button>      </>
      )
}

export default App
