import React, { useEffect, useState } from "react";
import axios from "axios";

const App = () => {
  // State variables to store the selected Pokemon ID, name, and moves
  const [num, setNum] = useState(1); // Default Pokemon ID is 1
  const [name, setName] = useState(""); // State for Pokemon name
  const [moves, setMoves] = useState([]); // State for Pokemon moves

  // useEffect is used to fetch data when the component mounts or when 'num' changes
  useEffect(() => {
    async function getData() {
      try {
        // Fetch Pokemon data from PokeAPI based on the selected ID (num)
        const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${num}`);//num to get different pokemon
        /*after writing it just once run it wit
        setname & setmoves go to inspect on site check for object, click on it, check for data in that you'll find name of pokemon and moves
        since it's in string  and many moves(so array is used) respectively in useState()  */
        
        // Update the state with the fetched data
        setName(response.data.name);
        setMoves(response.data.moves);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    }

    // Call the getData function when the component mounts or when 'num' changes
    getData();
  }, [num]); // 'num' is added as a dependency to run the effect when 'num' changes

  // JSX to render the component
  return (
    <>
      {/* Display the selected Pokemon ID */}
      <h1>
        You Choose <span style={{ color: "red" }}>{num}</span> value
      </h1>
      
      {/* Display the Pokemon name */}
      <h1>
        My name is <span style={{ color: "red" }}>{name}</span>
      </h1>
      
      {/* Display the Pokemon moves */}
      <h1>
        I have{" "}
        <span style={{ color: "red" }}>
          {/* Map through the 'moves' array and display each move */}
          {moves.map((move, index) => (
            <span key={index}>{move.move.name}, </span>
          ))}
        </span>
      </h1>
      
      {/* Dropdown to select a different Pokemon ID */}
      <select value={num} onChange={(e) => setNum(e.target.value)}>
        <option value="1">1</option>
        <option value="25">25</option>
        <option value="3">3</option>
        <option value="4">4</option>
        <option value="5">5</option>
      </select>
    </>
  );
};

export default App;
