import React, { useState } from "react";

const App=()=>{
    
    
    const [Fullname, setFullname] =useState({
        fname:"",
        lname:"",
        email:"",
        phone:"",
    })
    
   
    const inputEvent=(e) =>{
       const {name, value} = e.target
       setFullname((preValue) =>   {
            return{
                ...preValue,
                [name]:value
            }
            
       })

    }
    

   
    const onSubmit =(e) =>{
        e.preventDefault()// since after clicking on submit button, by default input text will be gone to make it stay
        //write preventDefault() function 
       
    }
    return (
       <>
         <div className="main_div">
           <form   >
            <div>
              <h1>Hello {Fullname.name} {Fullname.lname}</h1>
              <p>{Fullname.email}</p>
              <p>{Fullname.phone}</p>
              
              <input 
               type="text"
               placeholder="Enter" 
               name="fname"
               onChange={inputEvent}
               value={Fullname.fname}
              />
              <br/>
              <input 
               type="text"
               placeholder="Enter Last Name" 
               name="lname"
               onChange={inputEvent}
               value={Fullname.lname}
               
              />
              <input 
               type="email"
               placeholder="Enter Last Name" 
               name="email"
               onChange={inputEvent}
               value={Fullname.email}
               autoComplete="off"
               
              />
              <input 
               type="number"
               placeholder="Enter Last Name" 
               name="phone"
               onChange={inputEvent}
               value={Fullname.phone}
               
              />
              <button type="submit" onSubmit={onSubmit}>
                Submit
              </button>
            </div>
           </form>
         </div>
       
       
       
       </>
    )

    }   


export default App
