import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Card from "./Card";
import Sdata from "./Sdata";

const Courses = () => {
  const [filteredData, setFilteredData] = useState(Sdata);
  const { search } = useParams();

  // Update courses when the search term changes
  useEffect(() => {
    // Check if search is defined before filtering
    if (search) {
      const filteredCourses = Sdata.filter(
        (course) =>
          course.title.toLowerCase().includes(search.toLowerCase()) ||
          course.subtitle.toLowerCase().includes(search.toLowerCase())
      );
      setFilteredData(filteredCourses);
    } else {
      // If search is undefined, use the full data
      setFilteredData(Sdata);
    }
  }, [search]);

  return (
    <>
      <h1 className="heading_style"> Most Learned Courses </h1>
      {filteredData.map((val) => {
        return (
          <Card
            key={val.id}
            imgsrc={val.imgsrc}
            title={val.title}
            subtitle={val.subtitle}
            link={val.link}
          />
        );
      })}
    </>
  );
};

export default Courses;
