import React,{useState} from "react"

const App = () => {
        
          let h= new Date().getHours()
          let m= new Date().getMinutes()
          let s=new Date().getSeconds()
          let v= "00:00:00" 
     
     const [count,setCount] =useState(v)
     const Numclick=() => {
    
       setInterval(setCount(s),1000)
     }
      return (
        <>
           <h1 className="heading_style" > {count}</h1> 
           <button onClick={Numclick} >Click me</button>      </>
      )
}

export default App
