import React, { useState } from 'react';
import Card from "./Card" 
import Sdata from "./Sdata"
import DarkModeButton from './DarkModebtn';
// Import a CSS file for additional styling if needed

const App = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setIsDarkMode((prevMode) => !prevMode);
    document.body.classList.toggle('dark-mode'); // Toggle the 'dark-mode' class on the body
  };

  return (
    <>
      <DarkModeButton toggleDarkMode={toggleDarkMode} />

      <div className={`main-content ${isDarkMode ? 'dark-mode' : ''}`}>
        <h1 className="heading_style">Most Watched Netflix Webseries</h1>
        
        {Sdata.map((val) => (
          <Card 
            key={val.id}
            imgsrc={val.imgsrc}
            title={val.title}
            sname={val.sname}
            link={val.link} 
          /> 
        ))}
      </div>
    </>
  );
};

export default App;




