const App=()=>{
    const[name,setN] = useState("")
    const[f,setF]=useState()

    const inputEvent=(e) =>{
        setN(e.target.value)

    }

    const onSubmit =() =>{
        setF(name)
    }
    return (
       <>
         <div>
            <h1>Hello {f}</h1>
            <input type="text" placeholder="Enter" 
            onClick={inputEvent}/>
            <button onClick={onSubmit} > Click me üëç</button>
         </div>
       
       
       
       </>
    )
}

export default App
