import React from 'react'

function App(props){
  
    const [isDarkMode, setIsDarkMode] = useState(false);
  
    const toggleDarkMode = () => {
      setIsDarkMode((prevMode) => !prevMode);
      document.body.classList.toggle('dark-mode'); // Toggle the 'dark-mode' class on the body
    };
      
    return (
            <>
             
      <button onClick={toggleDarkMode}>
        {isDarkMode ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
      </button>
      <div style={{ background: isDarkMode ? '#333' : '#fff', color: isDarkMode ? '#fff' : '#333', padding: '20px' }}></div>
            <div clasName="cards">
              <div className="card">
                     
                      <img src={props.imgsrc} alt="myPic" className="card__img" />
                      <div className='card__info'>
                        <span className='card__category'>A Netflix original Series</span>
                        <h3 className='card__title'>{props.title}</h3>
                        <a href={props.link} target='_blank' rel="noreferrer">
                           <button>Watch Now</button>
                        </a>
                      </div>
              </div>
            </div>
                
        
        </>

    )
}

export default App
