import React, { useState } from 'react';
import Card from "./Card" 
import Sdata from "./Sdata"

const [isDarkMode, setIsDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setIsDarkMode((prevMode) => !prevMode);
  };
 

const App = () => (/* All Sdata object will come in "val" since using "map func" */
    <>
    
      
     <h1 className="heading_style"> Most Watched Netflix Webseries </h1>
     
     
     {Sdata.map((val) => {
        return (
          <>
            <Card 
              key={val.id}
              imgsrc={val.imgsrc}
              title={val.title}
              sname= {val.sname}
              link={val.link} 
            /> 
                 <div className={`App ${isDarkMode ? 'dark' : 'light'}`}>
                   <h1>{isDarkMode ? 'Dark Mode' : 'Light Mode'}</h1>
                     <button onClick={toggleDarkMode}>
                        {isDarkMode ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
                     </button>
                 </div>  
            
           </> 
        )   
     })}

    </>
)
       
   
    

export default App


//props(property)
/*
(open)<App
      
whatever can be initiated here like
imgsrc= "nnn.jpg" ...etc 
(closed)/>       // (here you might have multiple images to initated which will take time to write
so create a different Sdata.js file  where you will or get must be stored it in array of objects. the way to access it is below)

it can be said as property for other js for example for

Card.js file

function card(que){
    <>
      <h1>hi bro {que.imgsrc} </h1> // initaited property is being called here to show img
    </>
}
  */



//Without mapping Function

//to get array of object elements like

      /*
        <App>
        <h1 className="heading_style"> Most Watched Netflix Webseries </h1>

         <App 
         imgsrc={Sdata[0].imgsrc}   
         title={Sdata[0].title}
         sname= {Sdata[0].sname}
         link={Sdata[0].link} />
        
        <App 
        imgsrc={Sdata[1].imgsrc}
         title={Sdata[1].title}
         sname= {Sdata[1].sname}
         link={Sdata[1].link} /> 

        <App 
         imgsrc={Sdata[2].imgsrc}
         title={Sdata[2].title}
         sname= {Sdata[2].sname}
         link={Sdata[2].link} /> 


<App 
        imgsrc={Sdata[3].imgsrc}
        title={Sdata[3].title}
        sname= {Sdata[3].sname}
        link={Sdata[3].link} /> 
        
        <App 
         imgsrc={Sdata[4].imgsrc}
         title={Sdata[4].title}
         sname= {Sdata[4].sname}
         link={Sdata[4].link} /> 
         
         
        </>

*/


 

//With Mapping Function


 /* in mapping you could write {Sdata.map(ncard)} or {Sdata.map(function ncard(val) {                               or        {Sdata.map((val) => {                                          since map(variable,index,arrray) syntax is,so 
                                                                                                                                                                                                        here we r using variable   
                                                                                       return (                                                                    return (
                                                                                               <App                                                                         <App 
                                                                                                 imgsrc={val.imgsrc}                                                         imgsrc={val.imgsrc}
                                                                                                 title={val.title}                                                           title={val.title}
                                                                                                 sname= {val.sname}                                                          sname= {val.sname}
                                                                                                 link={val.link} />                                                          link={val.link} />  
                                                                                          
                                                                                               )                                                                           )
                                                                                                                              }
                                                                                                                              */
