import React, { useState } from "react";

const App=()=>{
    const[name,setN] = useState("")
    const[f,setF]=useState()

    const inputEvent=(e) =>{
        setN(e.target.value)

    }

    const onSubmit =(e) =>{
        e.preventDefault()// since after clicking on submit button, by default input text will be gone to make it stay
        //write preventDefault() function 
        setF(name)
    }
    return (
       <>
         <div className="main_div">
           <form  >
            <div>
              <h1>Hello {f}</h1>
              <input 
               type="text"
               placeholder="Enter" 
               onChange={inputEvent}
              />
              <br/>
              <button type="submit"  onClick={onSubmit}>
                Submit
              </button>
            </div>
           </form>
         </div>
       
       
       
       </>
    )
}

export default App
